import React from 'react';
import ReactDOM from 'react-dom/client';
import './homepage.css';
import imdbLogo from './images/imdblogo.png';
import dunePost from './images/dune-poster.png';

import dMov from './images/d-mov.png';
import pMov from './images/p-mov.png';
import kMov from './images/k-mov.png';
import sMov from './images/s-mov.png';
import fMov from './images/f-mov.png';
import kfMov from './images/kf-mov.png';

const movieImages = [
    'https://via.placeholder.com/150/0000FF/808080?Text=Movie1',
    'https://via.placeholder.com/150/FF0000/FFFFFF?Text=Movie2',
    'https://via.placeholder.com/150/FFFF00/000000?Text=Movie3',
    'https://via.placeholder.com/150/000000/FFFFFF?Text=Movie4',
    'https://via.placeholder.com/150/00FF00/000000?Text=Movie5',
  ];

const MovieDisplay = () => {
    const [visibleCount, setVisibleCount] = useState(movieImages.length);

    useEffect(() => {
        const handleResize = () => {
            if (window.innerWidth < 768) {
                setVisibleCount(3); // Show only 3 images on small screens
            } else {
                setVisibleCount(movieImages.length); // Show all images on larger screens
            }
        };

        // Listen for resize events
        window.addEventListener('resize', handleResize);

        // Call the resize handler once on component mount to set the initial state
        handleResize();

        // Cleanup the event listener on component unmount
        return () => window.removeEventListener('resize', handleResize);
    }, []);

    return (
        <div className="movies">
            {movieImages.slice(0, visibleCount).map((src, index) => (
                <div key={index} className="movie">
                    <img className="mov-img" src={src} alt={`Movie ${index + 1}`} />
                    <p className="mov-title">Movie {index + 1}</p>
                </div>
            ))}
        </div>
    );
};


function CircleScore({ score, size = 100, strokeWidth = 10 }) {
    const radius = (size - strokeWidth) / 2;
    const circumference = radius * 2 * Math.PI;
    const offset = circumference - (score / 100) * circumference;

    const circleStyle = {
      strokeDasharray: `${circumference} ${circumference}`,
      strokeDashoffset: offset,
    };

    return (
      <svg width={size} height={size} className="circle-score">
        {/* Background circle */}
        <circle
          cx={size / 2}
          cy={size / 2}
          r={radius}
          strokeWidth={strokeWidth}
          fill="transparent"
          stroke="#ddd" // Background circle color
        />
        {/* Foreground circle (score) */}
        <circle
          cx={size / 2}
          cy={size / 2}
          r={radius}
          strokeWidth={strokeWidth}
          fill="transparent"
          stroke="black" // Score circle color
          transform={`rotate(-90 ${size / 2} ${size / 2})`}
          style={circleStyle}
        />
        {/* Score text */}
        <text
          x="50%"
          y="50%"
          dominantBaseline="middle"
          textAnchor="middle"
          fill="#333"
          fontSize={size / 4}
          fontWeight="bold"
        >
          {score}%
        </text>
      </svg>
    );
  }


function Homepage() {
    return (
        <div className="main-page">
            <header>
                <nav className="navBar"> 
                    <div className="nav-cont"> 
                        {/* Left icons */}
                        <div className="nav-icons">
                            <img className="img" src={imdbLogo} alt="Logo" />
                        </div>
                        {/* Search bar */}
                        <div className="search-container">
                            <input className="search-bar" placeholder="Type something..." />
                        </div>
                        {/* Right buttons */}
                        <div className="nav-buttons">
                            <button className="sign-in">Sign-In</button>
                            <button className="en">En</button>
                        </div>
                    </div>
                </nav>
            </header>
            <main>
                {/* Movie Posters (titles, ratings, small desc) */}
                <div className="movie-slider">
                    <img className="dune-img" src={dunePost} alt="Dune Part Two" />
                    {/* this allows stuff over the img */}
                    <div className="overlay-content"> 
                        <p className="dune-mov-title">Dune: Part Two</p>
                        <p className="dune-mov-des">2h 46m / sci-fi, action, adventure</p>
                        {/* holds watch btn and score  */}
                        <div className="watch-score-cont"> 
                            <button className="watch-btn">Watch Trailer</button>
                            <CircleScore score={90} />
                        </div>
                    </div>
                </div>
                <div className = "movie-ctn"> 
                    <header> 
                        <nav className="navBar"> 
                            <div className="nav-cont"> 
                                <div className = "titles">
                                    <button className = "mov-tit">MOVIES</button>
                                    <button className = "tv-tit">TV SERIES</button>
                                    <button className = "ppl-tit">PEOPLE</button>
                                    <button className = "com-tit">COMMUNITY</button>
                                </div> 
                            </div>
                            <div className="Line" style={{width: '100%', height: '100%', border: '1px white solid'}}></div>
                        </nav>
                    </header> 
                    <p className = "mov-title"> See What's Trending Now</p>
                    <div className = "mov-holder">
                      <MovieDisplay/>
                    </div>
                </div> 
            </main>
        </div>
    );
}

export default Homepage;
